<?php//************************************************// DO NOT USE THE DEFAULT TEMPLATE FOR INNOVATING NEW THEMES! // INSTEAD USE ANY OTHER THEME FOUND IN THE /TEMPLATES/ DIRECTORY// IGNORE EVERYTHING BELOW!// Note: When creating your own template... instructions to come //gets template directory name to use as identifier - do not edit - include in all template files$key = lp_get_parent_directory(dirname(__FILE__)); //EDIT - START - defines template information - helps categorizae template and provides additional popup information$lp_data[$key]['category'] = "Theme Integrated"; $lp_data[$key]['version'] = "1.0.0.1"; $lp_data[$key]['description'] = "This template integrates with your currently active wordpress theme. Custom CSS and JS may be required to remove theme elements to achive a full page width."; //$lp_data[$key]['thumbnail'] = LANDINGPAGES_URLPATH.'templates/'.$key.'/thumbnail.png'; //DO NOT EDIT - adds template to template selection dropdown $lp_data[$key]['value'] = $key; //do not edit this$lp_data[$key]['label'] = ucwords(str_replace('-',' ',$key)); //do not edit this//************************************************// Where Should Form Go on Page?// Add a radio button option to your theme's options panel.	$options = array('widget'=>'Use Sidebar Widget (default)','right'=>'Float Form Area Right', 'left'=>'Float Form Area Left','bottom'=>'Insert Below Content','top'=>'Insert Above Content');$lp_data[$key]['options'][] = 	lp_add_option($key,"dropdown","conversion-area-placement","widget","Conversion/Form Placement","Where should the conversion area show on the page? When setting to 'Use Widget' make sure that the 'Conversion Area Widget' is added to the 'Landing Page Sidebar' for the conversion area to display properly.", $options);$options = array('off'=>'Hide Navigation (recommended)','on'=>'Show Navigation');$lp_data[$key]['options'][] = 	lp_add_option($key,"dropdown","lp_hide_nav","off","Navigation Settings","Toggle the regular navigation on or off with this setting. It's highly recommended that you turn off your page navigation to increase conversion rates on your landing page", $options);	// Add additional options by creating a custom-functions.php file and uploading it in the main template directory if(file_exists( dirname(__FILE__) . '/custom-functions.php')) {   	include dirname(__FILE__) . "/custom-functions.php";	}